var kkpager = {
    //divID
    pagerid: 'div_pager',
    //当前页码
    pno: 1,
    //总页码
    total: 1,
    //总数据条数
    totalRecords: 0,
    //是否显示总页数
    isShowTotalPage: true,
    //是否显示总记录数
    isShowTotalRecords: true,
    //是否显示页码跳转输入框
    isGoPage: true,
    //链接前部
    hrefFormer: '',
    //链接尾部
    hrefLatter: '',
    ///按钮事件名称
    clickName: '',
    //名字
    lang: {},
    ///类型
    mode: 'click',
    /****链接算法****/
    GreatUrl: function (n) {
        if (this.mode == 'click') {
            return 'onclick="' + this.clickName + '(' + n + ')"';
        }
        else {
            //这里的算法适用于比如：
            //hrefFormer=http://www.xx.com/news/20131212
            //hrefLatter=.html
            //那么首页（第1页）就是http://www.xx.com/news/20131212.html
            //第2页就是http://www.xx.com/news/20131212_2.html
            //第n页就是http://www.xx.com/news/20131212_n.html
            if (n == 1) {
                return 'href=' + this.hrefFormer + this.hrefLatter;
            } else {
                return 'href=' + this.hrefFormer + '_' + n + this.hrefLatter;
            }
        }


    },
    //跳转框得到输入焦点时
    focus_gopage: function () {
        var btnGo = $('#btn_go');
        $('#btn_go_input').attr('hideFocus', true);
        btnGo.show();
        btnGo.css('left', '0px');
        $('#go_page_wrap').css('border-color', '#6694E3');
        btnGo.animate({ left: '+=44' }, 50, function () {
            //$('#go_page_wrap').css('width','88px');
        });
    },

    //跳转框失去输入焦点时
    blur_gopage: function () {
        setTimeout(function () {
            var btnGo = $('#btn_go');
            //$('#go_page_wrap').css('width','44px');
            btnGo.animate({
                left: '-=44'
            }, 100, function () {
                $('#btn_go').css('left', '0px');
                $('#btn_go').hide();
                $('#go_page_wrap').css('border-color', '#DFDFDF');
            });
        }, 400);
    },
    //跳转框页面跳转
    gopage: function () {
        var str_page = $("#btn_go_input").val();
        if (isNaN(str_page)) {
            $("#btn_go_input").val(this.next);
            return;
        }
        var n = parseInt(str_page);
        if (n < 1 || n > this.total) {
            $("#btn_go_input").val(this.next);
            return;
        }
        if (this.mode == 'click') {
            eval(this.clickName + '(' + n + ')');
        }
        else {
            //这里可以按需改window.open
            window.location = this.clickName(n);
        }

    },
    //分页按钮控件初始化
    init: function (config) {
        //赋值
        this.pno = isNaN(config.pno) ? 1 : parseInt(config.pno);
        this.total = isNaN(config.total) ? 1 : parseInt(config.total);
        this.totalRecords = isNaN(config.totalRecords) ? 0 : parseInt(config.totalRecords);
        if (config.pagerid) { this.pagerid = config.pagerid; }
        if (config.isShowTotalPage != undefined) { this.isShowTotalPage = config.isShowTotalPage; }
        if (config.isShowTotalRecords != undefined) { this.isShowTotalRecords = config.isShowTotalRecords; }
        if (config.isGoPage != undefined) { this.isGoPage = config.isGoPage; }
        this.hrefFormer = config.hrefFormer || '';
        this.hrefLatter = config.hrefLatter || '';
        //名字
        if (config.lang) {
            for (var key in config.lang) {
                this.lang[key] = config.lang[key];
            }
        }
        //if (config.clickName && typeof (config.clickName) == 'function') {
        //    this.clickName = config.clickName;
        //}
        this.clickName = config.clickName;
        //验证
        if (this.pno < 1) this.pno = 1;
        this.total = (this.total <= 1) ? 1 : this.total;
        if (this.pno > this.total) this.pno = this.total;
        this.prv = (this.pno <= 2) ? 1 : (this.pno - 1);
        this.next = (this.pno >= this.total - 1) ? this.total : (this.pno + 1);
        this.hasPrv = (this.pno > 1);
        this.hasNext = (this.pno < this.total);

        this.inited = true;
    },
    //生成分页控件Html
    generPageHtml: function () {
        if (!this.inited) {
            return;
        }

        var str_prv = '', str_next = '';
        if (this.hasPrv) {

            str_prv = '<a ' + this.GreatUrl(this.prv) + ' title="'
                    + (this.lang.prePageTipText || this.lang.prePageText) + '">' + this.lang.prePageText + '</a>';


        } else {
            str_prv = '<span class="disabled">' + this.lang.prePageText + '</span>';
        }

        if (this.hasNext) {
            str_next = '<a ' + this.GreatUrl(this.next) + ' title="'
                    + (this.lang.nextPageTipText || this.lang.nextPageText) + '">' + this.lang.nextPageText + '</a>';
        } else {
            str_next = '<span class="disabled">' + this.lang.nextPageText + '</span>';
        }


        var str = '';
        var dot = '<span>...</span>';
        var total_info = '';
        if (this.isShowTotalPage || this.isShowTotalRecords) {
            total_info = '<span class="normalsize">' + this.lang.totalPageBeforeText;
            if (this.isShowTotalPage) {
                total_info += this.total + this.lang.totalPageAfterText;
                if (this.isShowTotalRecords) {
                    total_info += '&nbsp;/&nbsp;';
                }
            }
            if (this.isShowTotalRecords) {
                total_info += this.totalRecords + this.lang.totalRecordsAfterText;
            }

            total_info += '</span>';
        }

        var gopage_info = '';
        if (this.isGoPage) {
            gopage_info = '&nbsp;' + this.lang.gopageBeforeText + '<span id="go_page_wrap" style="display:inline-block;width:44px;height:18px;border:1px solid #DFDFDF;margin:0px 1px;padding:0px;position:relative;left:0px;top:5px;">' +
                '<input type="button" id="btn_go" onclick="kkpager.gopage();" style="width:44px;height:20px;line-height:20px;padding:0px;font-family:arial,宋体,sans-serif;text-align:center;border:0px;background-color:#0063DC;color:#FFF;position:absolute;left:0px;top:-1px;display:none;" value="' + this.lang.gopageButtonOkText + '" />' +
                '<input type="text" id="btn_go_input" onfocus="kkpager.focus_gopage()" onkeypress="if(event.keyCode<48 || event.keyCode>57)return false;" onblur="kkpager.blur_gopage()" style="width:42px;height:16px;text-align:center;border:0px;position:absolute;left:0px;top:0px;outline:none;" value="' + this.pno + '" /></span>' + this.lang.totalPageAfterText;
        }

        //分页处理
        if (this.total <= 8) {
            for (var i = 1; i <= this.total; i++) {
                if (this.pno == i) {
                    str += '<span class="curr">' + i + '</span>';
                } else {
                    str += '<a ' + this.GreatUrl(i) + ' title="' + this.lang.buttonTipBeforeText + i + this.lang.gopageAfterText + '">' + i + '</a>';
                }
            }
        } else {
            if (this.pno <= 5) {
                for (var i = 1; i <= 7; i++) {
                    if (this.pno == i) {
                        str += '<span class="curr">' + i + '</span>';
                    } else {
                        str += '<a ' + this.GreatUrl(i) + ' title="' + this.lang.buttonTipBeforeText + i + this.lang.gopageAfterText + '">' + i + '</a>';
                    }
                }
                str += dot;
            } else {
                str += '<a ' + this.GreatUrl(1) + ' title="第1页">1</a>';
                str += '<a ' + this.GreatUrl(2) + ' title="第2页">2</a>';
                str += dot;

                var begin = this.pno - 2;
                var end = this.pno + 2;
                if (end > this.total) {
                    end = this.total;
                    begin = end - 4;
                    if (this.pno - begin < 2) {
                        begin = begin - 1;
                    }
                } else if (end + 1 == this.total) {
                    end = this.total;
                }
                for (var i = begin; i <= end; i++) {
                    if (this.pno == i) {
                        str += '<span class="curr">' + i + '</span>';
                    } else {
                        str += '<a ' + this.GreatUrl(i) + ' title="' + this.lang.buttonTipBeforeText + i + this.lang.gopageAfterText + '">' + i + '</a>';
                    }
                }
                if (end != this.total) {
                    str += dot;
                }
            }
        }

        str = "&nbsp;" + str_prv + str + str_next + total_info + gopage_info;
        $("#" + this.pagerid).html(str);
        this.pagerid = "div_pager";
    }
};
